VALUES ('Amortizaciones', 'amortizaciones', 'phantom-pdf', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n\r\n<head>\r\n	<meta charset=\"UTF-8\">\r\n	<meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n	<meta http-equiv=\"X-UA-Compatible\" content=\"ie=edge\">\r\n	<title>Reporte Amortizaciones</title>\r\n	<style>\r\n		* {\r\n			box-sizing: border-box;\r\n			color: #4a4a4a;\r\n			font-family: Arial;\r\n			font-size: 11.5px;\r\n		}\r\n		\r\n		html,\r\n		body {\r\n			padding: 0px;\r\n			margin: 0px;\r\n		}\r\n		\r\n		table {\r\n			border-collapse: collapse;\r\n			border: 0.5px solid #000000;\r\n		}\r\n		\r\n		p {\r\n			margin: 0px;\r\n		}\r\n		\r\n		hr {\r\n			margin: 0px;\r\n		}\r\n		\r\n		.bg-japi {\r\n			background-color: #05579b;\r\n		}\r\n		\r\n		.bg-blue {\r\n			background-color: #1f87e5;\r\n		}\r\n		\r\n		.color-white {\r\n			color: #ffffff;\r\n		}\r\n		\r\n		.color-blue {\r\n			color: #05579b;\r\n		}\r\n		\r\n		.fw-bold {\r\n			font-weight: bold;\r\n		}\r\n		\r\n		.fw-semibold {\r\n			font-weight: 600;\r\n		}\r\n		\r\n		.mb-0 {\r\n			margin-bottom: 0px;\r\n		}\r\n		\r\n		.ml-0 {\r\n			margin-left: 0px;\r\n		}\r\n		\r\n		.mr-0 {\r\n			margin-right: 0px;\r\n		}\r\n		\r\n		.mt-0 {\r\n			margin-top: 0px;\r\n		}\r\n		\r\n		.mx-0 {\r\n			margin-left: 0px;\r\n			margin-right: 0px;\r\n		}\r\n		\r\n		.my-0 {\r\n			margin-bottom: 0px;\r\n			margin-top: 0px;\r\n		}\r\n		\r\n		.pa-0 {\r\n			padding: 0px;\r\n		}\r\n		\r\n		.pb-0 {\r\n			padding-bottom: 0px;\r\n		}\r\n		\r\n		.pl-0 {\r\n			padding-left: 0px;\r\n		}\r\n		\r\n		.pr-0 {\r\n			padding-right: 0px;\r\n		}\r\n		\r\n		.pt-0 {\r\n			padding-top: 0px;\r\n		}\r\n		\r\n		.px-0 {\r\n			padding-left: 0px;\r\n			padding-right: 0px;\r\n		}\r\n		\r\n		.py-0 {\r\n			padding-bottom: 0px;\r\n			padding-top: 0px;\r\n		}\r\n		\r\n		.ta-center {\r\n			text-align: center;\r\n		}\r\n		\r\n		.ta-justify {\r\n			text-align: justify;\r\n		}\r\n		\r\n		.ta-left {\r\n			text-align: left;\r\n		}\r\n		\r\n		.ta-right {\r\n			text-align: right;\r\n		}\r\n		\r\n		.tt-uppercase {\r\n			text-transform: uppercase;\r\n		}\r\n		\r\n		.pl-34 {\r\n			padding-left: 34px;\r\n		}\r\n		\r\n		.pl-30 {\r\n			padding-left: 30px;\r\n		}\r\n		\r\n		.pr-32 {\r\n			padding-right: 32px;\r\n		}\r\n		\r\n		.pt-9 {\r\n			padding-top: 9px;\r\n		}\r\n		\r\n		.pt-25 {\r\n			padding-top: 25px;\r\n		}\r\n		\r\n		.px-26 {\r\n			padding-left: 26px;\r\n			padding-right: 26px;\r\n		}\r\n		\r\n		.px-5 {\r\n			padding-left: 5px;\r\n			padding-right: 5px;\r\n		}\r\n		\r\n		.pb-7 {\r\n			padding-bottom: 7px;\r\n		}\r\n		\r\n		.pr-18 {\r\n			padding-right: 18px;\r\n		}\r\n		\r\n		.h-36 {\r\n			height: 36px;\r\n		}\r\n		\r\n		.h-27 {\r\n			height: 27px;\r\n		}\r\n		\r\n		.fs-11 {\r\n			font-size: 11px;\r\n		}\r\n		\r\n		.fs-12 {\r\n			font-size: 12px;\r\n		}\r\n		\r\n		.fs-16 {\r\n			font-size: 16px;\r\n		}\r\n		\r\n		.fs-20 {\r\n			font-size: 20px;\r\n		}\r\n		\r\n		.border-0 {\r\n			border: 0px;\r\n		}\r\n		\r\n		.lh-27 {\r\n			line-height: 27px;\r\n		}\r\n		\r\n		table tbody tr:nth-child(even) td {\r\n			background-color : #f1f3f7;\r\n		}\r\n		\r\n		.subheader {\r\n			float: left;\r\n			height: 50px;\r\n			line-height: 50px;\r\n			width: 50%;\r\n		}\r\n		\r\n		.subheader-container {\r\n			border-bottom: 2px solid #05579b;\r\n		}\r\n		\r\n		.subheader-container:after {\r\n			clear: both;\r\n		}\r\n		\r\n		.subheader-container:after,\r\n		.subheader-container:before {\r\n			content: \'\';\r\n			display: table;\r\n		}\r\n	</style>\r\n</head>\r\n\r\n<body>\r\n	<div class=\"info-container\">\r\n		<div class=\"bg-japi pl-34 pt-9 pb-7\">\r\n			<img\r\n				src=\"https://s3.amazonaws.com/japi-admin/logo-japi-white.png\"\r\n				height=\"25px\"\r\n			/>\r\n		</div>\r\n		<div class=\"subheader-container\">\r\n			<div class=\"subheader pl-30\">\r\n				<span class=\"fs-12\">{{ date }}</span>\r\n			</div>\r\n			<div class=\"subheader ta-right pr-32\">\r\n				<span class=\"color-blue fs-16 fw-semibold\">Total:</span>\r\n				<span class=\"color-blue fs-20 fw-bold\">{{ currencySymbol }}{{ total }}</span>\r\n			</div>\r\n		</div>\r\n		<div class=\"px-26 pt-25\">\r\n			<table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" class=\"ta-center border-0\">\r\n				<thead>\r\n					<tr>\r\n						<td class=\"bg-blue ta-center color-white fs-11 h-36 fw-semibold\"># Amortización</td>\r\n						<td class=\"bg-blue ta-center color-white fs-11 h-36 fw-semibold\">Fecha de Pago</td>\r\n						<td class=\"bg-blue ta-left color-white fs-11 h-36 fw-semibold\">Empleado</td>\r\n						<td class=\"bg-blue ta-center color-white fs-11 h-36 fw-semibold\">Tipo de Pago</td>\r\n						<td class=\"bg-blue ta-center color-white fs-11 h-36 fw-semibold\">Monto</td>\r\n					</tr>\r\n				</thead>\r\n				<tbody>\r\n					{{#each result}}\r\n					<tr>\r\n					   <td class=\"h-27 fw-semibold\">{{ id }}</td>\r\n					   <td class=\"h-27 fw-semibold\">{{ date }}</td>\r\n					   <td class=\"h-27 ta-left fw-semibold\">{{ employeeName }}</td>\r\n					   <td class=\"h-27 fw-semibold\">{{ typePaymentName }}</td>\r\n					   <td class=\"h-27 fw-semibold\">{{ currencySymbol }}{{ amount }}</td>\r\n					</tr>\r\n					{{/each}}\r\n				</tbody>\r\n			</table>\r\n			<div class=\"bg-blue h-27 tt-uppercase color-white ta-right pr-18 lh-27 fw-semibold\">\r\n				Total: {{ currencySymbol }}{{ total }}\r\n			</div>\r\n		</div>\r\n	</div>\r\n</body>\r\n\r\n</html>\r\n\r\n\r\n\r\n', 
1, '1', NULL, '1', NULL, now(), now());

INSERT INTO `ms_templates` (`name`, `code`, `recipe`, `template`, `country_id`, `flag_default`, `type_document_id`, `flag_active`, `deleted_at`, `created_at`, `updated_at`)
VALUES ('Arqueo de caja', 'arqueoCaja', 'phantom-pdf', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n	<meta charset=\"UTF-8\">\r\n	<meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n	<meta http-equiv=\"X-UA-Compatible\" content=\"ie=edge\">\r\n	<link href=\"https://fonts.googleapis.com/css?family=Open+Sans:400,600,700,800\" rel=\"stylesheet\">\r\n	<title>Report</title>\r\n	\r\n	<style>\r\n	* {\r\n	    box-sizing: border-box;\r\n	    font-family: \'Open Sans\', sans-serif;\r\n	  }\r\n	\r\n	.header {\r\n	    background:#05579b;\r\n	    margin-bottom:21px;\r\n	    padding:10px 0px 5px 30px;\r\n\r\n	}\r\n	.center {\r\n	    text-align:center;\r\n	}\r\n\r\n	table{\r\n        border-collapse: collapse;\r\n        margin-left:30px;\r\n        margin-right:30px;\r\n        width:92%;\r\n    }\r\n    \r\n    .end {\r\n        float: right;\r\n    }\r\n    \r\n    .title {\r\n       font-size:18px;    \r\n       font-weight: 400; \r\n       margin-bottom:12px;\r\n       padding-left:30px;\r\n    }\r\n    \r\n    hr {\r\n        background-color:#05579b;\r\n        border:none;\r\n        height:2px;\r\n        margin-bottom:27px;\r\n    }\r\n    \r\n    tr:nth-child(odd) {\r\n        background-color: #f2f2f2\r\n        \r\n    }\r\n    \r\n    th, tr:last-child{\r\n        background-color: #0080e9;\r\n        color: white;\r\n       \r\n    }\r\n    \r\n    th,td {\r\n        padding:10px;\r\n    }\r\n    \r\n    th:nth-child(2) {\r\n      text-align:left;\r\n      padding-left:150px;\r\n    }\r\n    \r\n     td:nth-child(1), td:nth-child(3){\r\n        text-align:center;\r\n    }\r\n    \r\n    th {\r\n         font-weight:200;\r\n    }\r\n    \r\n    .logo {\r\n        height:25px;\r\n        width:69px;\r\n    }\r\n    \r\n    .pad-lef {\r\n        padding-left:30px;\r\n    }\r\n    \r\n    .pad-right {\r\n        padding-right:30px;\r\n    }\r\n    \r\n    .span-title {\r\n        font-size:14px;\r\n    }\r\n    \r\n    .span-date {\r\n        font-size:12px;\r\n    }\r\n    \r\n    .font-max {\r\n        font-size:13px;\r\n    }\r\n    \r\n    .font-min {\r\n        font-size:11.5px;\r\n    }\r\n  \r\n	</style>\r\n</head>\r\n<body>\r\n    <div class=\"header\">\r\n        <img src=\"https://s3.amazonaws.com/japi-admin/logo-japi-white.png\" class=\"logo\"/>\r\n    </div>\r\n    <div>\r\n        <span class=\"pad-lef span-title\">{{company_name}}</span>\r\n        <span class=\"end pad-right span-date\">{{date}}</span>\r\n    </div>\r\n    <h1 class=\"title\">\r\n        ARQUEO DE CAJA\r\n    </h1>\r\n    <span class=\"pad-lef font-min\">Desde: {{startDate}} - Hasta: {{endDate}}</span>\r\n    <hr>\r\n	<table>\r\n	   <tr>\r\n	    <th class=\"font-max\">Nº de Cliente</th>\r\n	    <th class=\"font-max\">Nombre</th>\r\n	    <th class=\"font-max\">Total</th>\r\n	   </tr>\r\n	    {{#each customers}}\r\n	   <tr>\r\n	    <td class=\"font-min\">{{id}}</td>\r\n	    <td class=\"font-min\">{{typePerson.fullName}}</td>\r\n	    <td class=\"font-min\">S/.{{amountCustomer}}</td>\r\n	   </tr>\r\n	   {{/each}}\r\n	   <tr>\r\n	    <td class=\"font-max\">Nº de Clientes:{{total}}</td>\r\n	    <td></td>\r\n	    <td class=\"font-min\">TOTAL: S/.{{totalAmount}}</td>\r\n	   </tr>\r\n	</table>\r\n\r\n</body>\r\n</html>', 
1, '1', NULL, '1', NULL, now(), now());

INSERT INTO `ms_templates` (`name`, `code`, `recipe`, `template`, `country_id`, `flag_default`, `type_document_id`, `flag_active`, `deleted_at`, `created_at`, `updated_at`)
VALUES ('Articulos Vendidos', 'articulosVendidos', 'phantom-pdf', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n	<meta charset=\"UTF-8\">\r\n	<meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n	<meta http-equiv=\"X-UA-Compatible\" content=\"ie=edge\">\r\n	<link href=\"https://fonts.googleapis.com/css?family=Open+Sans:400,600,700,800\" rel=\"stylesheet\">\r\n	<title>Report</title>\r\n	\r\n	<style>\r\n	* {\r\n	    box-sizing: border-box;\r\n	    font-family: \'Open Sans\', sans-serif;\r\n	  }\r\n	\r\n	.header {\r\n	    background:#05579b;\r\n	    margin-bottom:21px;\r\n	    padding:10px 0px 5px 30px;\r\n	}\r\n\r\n	table{\r\n        border-collapse: collapse;\r\n        margin-bottom:20px;\r\n        margin-left:30px;\r\n        width:92%;\r\n    }\r\n    \r\n    .title {\r\n       font-size:18px;\r\n       font-weight: 400; \r\n       padding-left:30px;\r\n    }\r\n    \r\n    hr {\r\n        background-color:#05579b;\r\n        border:none;\r\n        height:2px;\r\n        margin-top:20px;\r\n        margin-bottom:27px;\r\n    }\r\n    \r\n    tr:nth-child(odd) {\r\n        background-color: #f2f2f2\r\n        \r\n    }\r\n    \r\n    th, tr:last-child {\r\n        background-color: #0080e9;\r\n        color: white;\r\n    }\r\n    \r\n    th,td {\r\n        padding:10px;\r\n    }\r\n    \r\n    td:nth-child(1), td:nth-child(4), td:nth-child(3) {\r\n        text-align:center;\r\n    }\r\n    \r\n    th:nth-child(2) {\r\n        text-align:left;\r\n        width:400px;\r\n    }\r\n    \r\n    .logo {\r\n        height:25px;\r\n        width:69px;\r\n    }\r\n    \r\n    th {\r\n        font-weight:200;\r\n    }\r\n    \r\n    .container-category {\r\n        background:#f1f3f7;\r\n        border:1px solid #05579b;\r\n        border-radius:5px;\r\n        margin-bottom:20px;\r\n        margin-left:30px;\r\n        padding:8px 35px 7px 17px;\r\n        width:280px;\r\n    }\r\n    \r\n    .no-margin {\r\n        margin:0px !important;\r\n    }\r\n    \r\n     .font-max {\r\n        font-size:13px;\r\n    }\r\n    \r\n    .font-min {\r\n        font-size:11.5px;\r\n    }\r\n    \r\n    .pl-30 {\r\n        padding-left: 30px;\r\n    }\r\n	</style>\r\n</head>\r\n<body>\r\n  <div class=\"header\">\r\n    <img src=\"https://s3.amazonaws.com/japi-admin/logo-japi-white.png\" class=\"logo\"/>\r\n  </div>\r\n  <div>\r\n    <span class=\"title\">ARTICULOS VENDIDOS (POR FAMILIA)</span>\r\n    <p class=\"font-min pl-30\">Desde: {{startDate}} - Hasta: {{endDate}}</p>\r\n  </div>\r\n  <hr>\r\n  {{#each result}}\r\n  <div class=\"container-category\">\r\n      <p class=\"no-margin\">Categoría: <strong>{{categoryName}}</strong></p>\r\n  </div>\r\n  	<table>\r\n	   <tr>\r\n	    <th class=\"font-max\">Cod.</th>\r\n	    <th class=\"font-max\">Nombre</th>\r\n	    <th class=\"font-max\">Cant.</th>\r\n	    <th class=\"font-max\">Und.</th>\r\n	   </tr>\r\n	   {{#each products}}\r\n	   <tr>\r\n	    <td class=\"font-min\">{{code}}</td>\r\n	    <td class=\"font-min\">{{caracteristicas}}</td>\r\n	    <td class=\"font-min\">{{quantity}}</td>\r\n	    <td class=\"font-min\">{{unidad}}</td>\r\n	   </tr>\r\n	   {{/each}}\r\n	    <tr>\r\n	     <td></td>\r\n	     <td></td>\r\n	     <td class=\"font-max\">{{categoryQuantity}}</td>\r\n	     <td></td> \r\n	   </tr>\r\n	</table>\r\n {{/each}}\r\n</body>',
