 INSERT INTO `ms_templates` (`name`, `code`, `recipe`, `template`, `country_id`, `flag_default`, `type_document_id`, `flag_active`, `deleted_at`, `created_at`, `updated_at`)
VALUES ('Beneficios por venta', 'beneficioVenta', 'phantom-pdf', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n	<meta charset=\"UTF-8\">\r\n	<meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n	<meta http-equiv=\"X-UA-Compatible\" content=\"ie=edge\">\r\n	<link href=\"https://fonts.googleapis.com/css?family=Open+Sans:400,600,700,800\" rel=\"stylesheet\">\r\n	<title>Report</title>\r\n	\r\n	<style>\r\n	* {\r\n	    box-sizing: border-box;\r\n	    font-family: \'Open Sans\', sans-serif;\r\n	  }\r\n	  \r\n	  .logo {\r\n        height:25px;\r\n        width:69px;\r\n    }\r\n	\r\n	.header {\r\n	    background:#05579b;\r\n	    margin-bottom:21px;\r\n	    padding:10px 0px 5px 30px;\r\n	}\r\n\r\n	table{\r\n        border-collapse: collapse;\r\n        margin-bottom:0px;\r\n        font-size:14px;\r\n        width: 92%;\r\n        margin-left:30px;\r\n    }\r\n    \r\n    .title {\r\n       font-size:18px;\r\n       font-weight: 400; \r\n       margin-bottom:12px;\r\n       padding-left:30px;\r\n    }\r\n    \r\n    hr {\r\n        background-color:#05579b;\r\n        border:none;\r\n        height:2px;\r\n        margin-bottom:27px;\r\n    }\r\n    \r\n    tr:nth-child(odd) {\r\n        background-color: #f2f2f2\r\n        \r\n    }\r\n    \r\n    th {\r\n        background-color: #0080e9;\r\n        color: white;\r\n        font-weight:200;\r\n    }\r\n    \r\n    th,td {\r\n        padding:10px;\r\n    }\r\n    \r\n    .container-info {\r\n        background:#f1f3f7;\r\n        border:1px solid #05579b;\r\n        border-radius:5px;\r\n        font-size:12px;\r\n        margin:20px 30px 30px 30px;\r\n        padding:12px 10px;\r\n        text-align:center;\r\n        \r\n    }\r\n    \r\n    .container-info>span:nth-child(2), .container-info>span:nth-child(4){\r\n        margin-right:40px;\r\n    }\r\n    \r\n    .number-facture {\r\n        color:#1f87e5;\r\n        padding-left:30px;\r\n        font-size:14px;\r\n        font-weight:700;\r\n    }\r\n   \r\n    .result-table {\r\n        background:#1f87e5;\r\n        color:white;\r\n        margin-bottom:20px;\r\n        overflow: hidden;\r\n        width:92%;\r\n        margin-left:30px;\r\n    }\r\n    \r\n    .result-table>p {\r\n        float:right;\r\n        margin-right:20px;\r\n        font-size:13px;\r\n    }\r\n    \r\n     td:nth-child(2), td:nth-child(3), td:nth-child(4), td:nth-child(5) , td:nth-child(6)  {\r\n        text-align:center;\r\n        width:140px;\r\n    }\r\n    \r\n    th:nth-child(1) {\r\n        text-align:left;\r\n    }\r\n    \r\n    .container-total {\r\n        float:right;\r\n        font-size:18px;\r\n        font-weight:600;\r\n    }\r\n    \r\n    .title-span {\r\n        font-size:16px;\r\n    }\r\n    \r\n    .font-max {\r\n        font-size:13px;\r\n    }\r\n    \r\n    .font-min {\r\n        font-size:11.5px;\r\n    }\r\n    \r\n    .pl-30 {\r\n        padding-left: 30px;\r\n    }\r\n    \r\n	</style>\r\n</head>\r\n<body>\r\n  <div class=\"header\">\r\n    <img src=\"https://s3.amazonaws.com/japi-admin/logo-japi-white.png\" class=\"logo\"/>\r\n  </div>\r\n  <div>\r\n    <p class=\"title\">BENEFICIO POR VENTA<span class=\"title-span\"> (TOTAL: <strong>S/. {{benefitTotal}} - {{percentageTotal}}%</strong>)</span></p>\r\n    <span class=\"pl-30 font-min\">Desde: {{startDate}} - Hasta: {{endDate}}</span>\r\n  </div>\r\n  {{#each result}}\r\n  <hr>\r\n   <div class=\"container-info\">\r\n        <span>Nº de Cliente: </span>\r\n        <span><strong>{{documentNumber}}</strong></span>\r\n        <span>Nombre: </span>\r\n        <span><strong>{{fullName}}</strong></span>\r\n        <span>Beneficio Cliente: </span>\r\n        <span><strong>S/.{{benefitCustomer}} - {{percentageCustomer}}%</strong></span>\r\n  </div>\r\n  {{#each documents}}\r\n  <div>\r\n      <p class=\"number-facture\">{{typeDocumentName}}: <span>{{documentNumber}}</span></p>\r\n    <div> \r\n     <table>\r\n	   <tr>\r\n	    <th class=\"font-max\">Art