VALUES ('Factura PDF', 'pdf', 'phantom-pdf', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <meta http-equiv=\"X-UA-Compatible\" content=\"ie=edge\">\r\n    <title>Factura PDF</title>\r\n    <style>\r\n        * {\r\n            font-family: \"Arial\", Monaco, monospace;\r\n            font-size: 10px;\r\n            font-weight: normal;\r\n            padding: 0px;\r\n        }\r\n\r\n        .billing-container {\r\n            margin: 0 auto;\r\n            max-width: 213px;\r\n        }\r\n\r\n        .billing-header {\r\n            text-align: center;\r\n        }\r\n\r\n        table {\r\n            border-collapse: collapse;\r\n        }\r\n\r\n        thead > tr {\r\n            border-bottom: 2px dotted grey;\r\n            border-top: 2px dotted grey;\r\n        }\r\n\r\n	    thead {\r\n            display: table-row-group;\r\n        }\r\n\r\n        h1 {\r\n            font-size: 14px;\r\n        }\r\n\r\n        h3 {\r\n            font-size: 9px;\r\n        }\r\n\r\n        p {\r\n            margin: 0px;\r\n        }\r\n\r\n        table {\r\n            margin-top: 10px;\r\n            padding: 0px;\r\n        }\r\n\r\n        td.td-padding-top {\r\n            padding-top: 6px;\r\n        }\r\n\r\n        td.td-padding-bottom {\r\n            padding-bottom: 2px;\r\n        }\r\n\r\n        .pd-right {\r\n            padding-right: 6px;\r\n        }\r\n\r\n        .text-center {\r\n            text-align: center;\r\n        }\r\n\r\n        .text-left {\r\n            text-align: left;\r\n        }\r\n\r\n        .text-right {\r\n            text-align: right;\r\n        }\r\n    </style>\r\n</head>\r\n<body>\r\n    <div class=\"billing-container\">\r\n        <div class=\"billing-header\">\r\n            <h1>{{ company.companyRzSocial }}</h1>\r\n            <h3>{{ subsidiary.rzSocial }}</h3>\r\n            <h3>RUC: {{ subsidiary.ruc }}</h3>\r\n            <h3>{{ subsidiary.address }}</h3>\r\n            <h3>Tienda: {{ warehouse.address }}</h3>\r\n        </div>\r\n        <div class=\"billing-data\">\r\n            <p>{{ localDate.date }}</p>\r\n            <p>Serie: {{ terminal.printCode }}</p>\r\n            <p>Cajero: {{ employee.name }}</p>\r\n            <p>Cliente: {{ customer.typePerson.fullName }}</p>\r\n            <p>RUC: {{ customer.ruc }}</p>\r\n            <p>DNI: {{ customer.dni }}</p>\r\n            <p>{{ customer.address }}</p>\r\n        </div>\r\n        <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n            <thead>\r\n                <tr>\r\n                    <th class=\"text-left\">SKU</th>\r\n                    <th>DESC</th>\r\n                    <th>CANT.</th>\r\n                    <th>PRECIO</th>\r\n                    <th class=\"text-right\">TOTAL</th>\r\n                </tr>\r\n            </thead>\r\n            <tbody>\r\n                {{#each details}}\r\n                <tr>\r\n                    <td colspan=\"2\" class=\"td-padding-top\">\r\n                        {{ product.code }}\r\n                    </td>\r\n                    <td class=\"text-center td-padding-top\">\r\n                        {{ quantity }}\r\n                    </td>\r\n                    <td class=\"text-center td-padding-top\">\r\n                        {{ price }}\r\n                    </td>\r\n                </tr>\r\n                <tr>\r\n                    <td colspan=\"5\">\r\n                        {{ product.name }}\r\n                    </td>\r\n                </tr>\r\n                <tr>\r\n                    <td colspan=\"3\" class=\"text-center td-padding-bottom\">\r\n                        Desc: {{ discount }}\r\n                    </td>\r\n                    <td class=\"text-center td-padding-bottom\">\r\n                        {{ priceWithOutDiscount }}\r\n                    </td>\r\n                    <td class=\"text-right td-padding-bottom\">\r\n                        {{ subTotal }}\r\n                    </td>\r\n                </tr>\r\n                {{/each}}\r\n            </tbody>\r\n        </table>\r\n        <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n            <tbody>\r\n                <tr>\r\n                    <td>{{ totalQuantity }} Unid(s) Sub Total:</td>\r\n                    <td class=\"text-right\">S/ {{ subtotal }}</td>\r\n                </tr>\r\n                <tr>\r\n                    <td class=\"text-right\">IGV 18.00000%:</td>\r\n                    <td class=\"text-right\">{{ taxes }}</td>\r\n                </tr>\r\n                <tr>\r\n                    <td class=\"text-right\">Total:</td>\r\n                    <td class=\"text-right\">S/ {{ amount }}</td>\r\n                </tr>\r\n            </tbody>\r\n        </table>\r\n        <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n            <tbody>\r\n                {{#each transactions}}\r\n                <tr>\r\n                    <td>PAGO: {{ typePayment.name }}</td>\r\n                    <td class=\"text-center\">{{ currencySymbol }} {{ amount }}</td>\r\n                </tr>\r\n                {{/each}}\r\n                <tr>\r\n                    <td>Ud. ahorró:</td>\r\n                    <td>{{ discount }}</td>\r\n                </tr>\r\n            </tbody>\r\n        </table>\r\n        <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n            <thead>\r\n                <tr>\r\n                    <th class=\"text-left\">TK-FACTURAS Nro</th>\r\n                    <th class=\"pd-right\">:</th>\r\n                    <th>{{ serie }} {{ number }}</th>\r\n                </tr>\r\n            </thead>\r\n            <tbody>\r\n				<tr>\r\n					<td>{{ commentary }}</td>\r\n				</tr>\r\n				<tr>\r\n					<td colspan=\"4\">\r\n						{{{ salSeries.description }}}\r\n					</td>\r\n				</tr>\r\n                <tr>\r\n                    <td class=\"td-padding-top\">Tipo de Documento</td>\r\n                    <td class=\"td-padding-top pd-right\">:</td>\r\n                    <td class=\"td-padding-top\">{{ typeDocument.name }}</td>\r\n                </tr>\r\n                <tr>\r\n                    <td>Nro Documento</td>\r\n                    <td class=\"pd-right\">:</td>\r\n                    <td>{{ typeDocument.qpCode }}{{ serie }} {{ number }}</td>\r\n                </tr>\r\n                <tr>\r\n                    {{#if documentRelated}}\r\n                    <td>Doc. Relacionado</td>\r\n                    <td class=\"pd-right\">:</td>\r\n                    <td>{{documentRelated.typeDocument.qpCode}}{{ documentRelated.documentNumber }}</td>>\r\n                    {{/if}}\r\n                </tr>\r\n                <tr>\r\n                    <td>Fecha Documento</td>\r\n                    <td class=\"pd-right\">:</td>\r\n                    <td>{{ localDate.date }}</td>\r\n                </tr>\r\n                <tr>\r\n                    <td>Nro Autorización</td>\r\n                    <td class=\"pd-right\">:</td>\r\n                    <td>{{ terminal.sunatCode }}</td>\r\n                </tr>\r\n                <tr>\r\n                    <td></td>\r\n                </tr>\r\n                <tr>\r\n                    <td>Fecha Autorización</td>\r\n                    <td class=\"pd-right\">:</td>\r\n                    <td>{{ terminal.authorizationDate }}</td>\r\n                </tr>\r\n                <tr>\r\n                    <td>Serie desde el</td>\r\n                    <td class=\"pd-right\">:</td>\r\n                    <td>000001 hasta el 9999999</td>\r\n                </tr>\r\n            </tbody>\r\n        </table>\r\n    </div>\r\n</body>\r\n</html>',
 1, '1', NULL, '1', NULL, now(), now());

 INSERT INTO `ms_templates` (`name`, `code`, `recipe`, `template`, `country_id`, `flag_default`, `type_document_id`, `flag_active`, `deleted_at`, `created_at`, `updated_at`)
VALUES ('Factura PDF', 'pdf', 'phantom-pdf', '<html>\r\n\r\n<head>\r\n	<meta content=\"text/html; charset=utf-8\" http-equiv=\"Content-Type\">\r\n	<meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\r\n<style>\r\n	 *{font-size: 10px;border-collapse: collapse;}\r\n	 h1{font-size: 20px;}\r\n	 p{margin: 0;}\r\n	 .container{padding-bottom: 50px;}\r\n	 .fecha{font-size: 10px; text-align: right; margin-bottom: 9px;}\r\n	 .cabecera{width: 100%; height: 12%;}\r\n	 .content{width: 100%;margin-bottom: 20px;}\r\n	 .pie{width: 100%;vertical-align: top;}\r\n	 .negrita{font-weight: bold;}\r\n	 .mayu{text-transform: uppercase;}\r\n	 .amarillo{background-color: #eded55;}\r\n	 .azul{background-color: #7fceef;}\r\n	 .border-cell{border: solid 1px black;}\r\n	 .text-align-R{text-align: right;}\r\n	 .t-cliente{margin-right: 40px; width: 420px;}\r\n	 .t-vendedor tr td{padding: 2px;}\r\n	 .content {padding-top:6px;}\r\n	 .content table{width: 100%;}\r\n	 .content table tr td{ border: 1px solid black; text-align: center;}\r\n\r\n	 .t-pie table{font-size: 10px;}\r\n\r\n	 .banco{margin: 15px 70px;border: solid 1px black;width: 40%;}\r\n	 .borderBottom{border-bottom: solid 1px black;}\r\n	 .spacer{width: 340px;}\r\n	 .marginleft{30px;}\r\n	 .spacer1{width: 200px;}\r\n	 .spacer2{width: 220px;}\r\n	 .peso{width: 39px;}\r\n	#peso{ border-left: 1px solid black;\r\n					border-right: 1px solid black;\r\n					border-bottom: 1px solid black;}\r\n	 .content .noBorder tr td{border: none;}\r\n	 .bottom{position: absolute; bottom: 0;}\r\n	 .fontSize1{font-size: 11px;}\r\n	 .totalWidth{width: 100px;}\r\n	</style>\r\n</head>\r\n\r\n<body>\r\n		<div class=\"container\">\r\n				<div style=\"text-align:center\">\r\n						<img src=\"{{company.logo}}\" style=\"width:700px;height:30px;margin-bottom:30px;\">\r\n				</div>\r\n				<div style=\"display:flex;justify-content:center;\">\r\n						<h2 style=\"text-align:center;font-size: 18px;\">\r\n								<span style=\"border:1px solid black;padding:7px;\">{{typeDocument.name}} {{serie}}-{{number}}</span>\r\n						</h2>\r\n				</div>\r\n		<div class=\"fecha\">\r\n				<span>Fecha: {{createdAt}}</span></br>\r\n		</div>\r\n		<div class=\"cabecera\">\r\n				<table class=\"t-cabecera\">\r\n						<tr>\r\n								<td>\r\n										<table class=\"t-cliente\">\r\n												<tr>\r\n														<td>Señor(es):</td>\r\n														<td class=\"negrita mayu\">\r\n																{{customer.typePerson.fullName}}\r\n														</td>\r\n												</tr>\r\n												<tr>\r\n														<td>Contacto:</td>\r\n														<td class=\"mayu\">{{customer.contact}}</td>\r\n												</tr>\r\n												<tr>\r\n														<td>RUC:</td>\r\n														<td class=\"mayu\">{{customer.typePerson.documentNumber}}</td>\r\n												</tr>\r\n												<tr>\r\n														<td>Dirección:</td>\r\n														<td class=\"mayu\">{{customer.address}}</td>\r\n												</tr>\r\n												<tr>\r\n														<td>Teléfono:</td>\r\n														<td class=\"mayu\">{{customer.phone}}</td>\r\n												</tr>\r\n												<tr>\r\n														<td>Forma de Pago:</td>\r\n														<td class=\"mayu\">{{payment.name}}</td>\r\n												</tr>\r\n										</table>\r\n								</td>\r\n								<td style=\"width:35px\"></td>\r\n								<td>\r\n										<table class=\"t-vendedor\">\r\n												<tr>\r\n														<td class=\"amarillo border-cell\"nowrap>Plazo de entrega</td>\r\n														<td class=\"amarillo border-cell\"nowrap>Stock Inmediato</td>\r\n												</tr>\r\n												<tr>\r\n														<td class=\"azul border-cell\">Plazo de entrega:</td>\r\n														<td class=\"azul border-cell\"nowrap>Importación {{import_days}} días</td>\r\n												</tr>\r\n												<tr>\r\n														<td>Vendedor:</td>\r\n														<td style=\"font-size:9px;\"nowrap>{{employee.name}} {{employee.lastname}}</td>\r\n												</tr>\r\n												<tr>\r\n														<td nowrap>Validez de la Oferta:</td>\r\n														<td>{{quotation.daysExpire}} días</td>\r\n												</tr>\r\n										</table>\r\n								</td>\r\n						</tr>\r\n				</table>\r\n		</div>\r\n		<div class=\"content\">\r\n				<table style=\"table-layout:fixed;\">\r\n						<tr>\r\n								<td class=\"negrita\" style=\"width:19px;overflow:hidden;text-align:center;\" rowspan=\"2\">Item</td>\r\n								<td class=\"negrita mayu\" style=\"width:56px;overflow:hidden;\" rowspan=\"2\">Código</td>\r\n								<td class=\"negrita mayu\" style=\"width:150px;overflow:hidden;\" rowspan=\"2\">descripción</td>\r\n								<td class=\"negrita mayu\" rowspan=\"2\" style=\"width:57px;overflow:hidden;\">marca</td>\r\n								<td class=\"negrita\" style=\"width:28px;overflow:hidden;\" rowspan=\"2\">\r\n									<span style=\"font-size:9px\">Descuento</span>\r\n								</td>\r\n								<td class=\"negrita\" style=\"width:90px;overflow:hidden;\" colspan=\"3\">Cant</td>\r\n								<td class=\"negrita\" style=\"width:28px;overflow:hidden;font-size:9px;\" rowspan=\"2\">P.Unit</td>\r\n								<td class=\"negrita\" style=\"width:30px;overflow:hidden;font-size:9px;\" rowspan=\"2\">Sub Total</td>\r\n						</tr>\r\n						 <tr>\r\n								<td class=\"negrita\" style=\"font-size:10px;\">Pedido</td>\r\n								<td class=\"negrita\" style=\"font-size:10px;\">Stock</td>\r\n								<td class=\"negrita\" style=\"font-size:10px;\">Import</td>\r\n						</tr>\r\n			{{#each details}}\r\n		<tr>\r\n								<td>{{@index}}</td>\r\n			<td class=\"negrita\"style=\"overflow:hidden;\"nowrap>{{product.code}}\r\n					<br>\r\n					{{#if product.location}}\r\n							({{product.location}})\r\n					{{/if}}\r\n			</td>\r\n			<td style=\"text-align:left;overflow:hidden;\"nowrap>\r\n					<table class=\"noBorder\" style=\"table-layout:fixed;\" cellspacing=\"0\" cellpadding=\"0\">\r\n							<tr>\r\n									<td style=\"text-align:left;overflow:hidden;\"nowrap>{{product.name}}\r\n									{{#if product.description}}\r\n													({{product.description}})\r\n											{{/if}}\r\n								 </td>\r\n\r\n							</tr>\r\n					</table>\r\n\r\n			</td>\r\n			<td>{{product.brandName}}</td>\r\n			<td style=\"font-size:9px;\">{{discount}}</td>\r\n			<td class=\"fontSize1\">{{quantity}}</td>\r\n								<td class=\"amarillo fontSize1\">{{stockQuantity}}</td>\r\n								<td class=\"azul fontSize1\">{{importQuantity}}</td>\r\n								<td style=\"text-align:right;\">{{price}}</td>\r\n								<td style=\"text-align:right;\">{{subTotal}}</td>\r\n		</tr>\r\n		{{/each}}\r\n				</table>\r\n		</div>\r\n		<div class=\"pie\">\r\n				<table class=\"t-pie\">\r\n						<tr>\r\n								<td style=\"text-align:left\">\r\n										<table class=\"garantia\">\r\n												<tr>\r\n														<td class=\"negrita\">Garantía:</td>\r\n														<td>la ofrecida por el fabricante</td>\r\n												</tr>\r\n												<tr>\r\n														<td class=\"negrita\"  style=\"border:1px solid black;padding:2px;\">Referencia:</td>\r\n														<td style=\"border:1px solid black;width:200px;padding:2px;font-weight:bold;\">{{commentary}}</td>\r\n												</tr>\r\n										</table>\r\n								</td>\r\n\r\n								<td style=\"width:260px\"></td>\r\n\r\n								<td style=\"padding:5px;border:1px solid black;text-align:right\">\r\n										<table>\r\n												<tr>\r\n														<td class=\"negrita fontSize1\"nowrap>Sub-Total:</td>\r\n														<td class=\"text-align-R fontSize1\" style=\"width:100px\">{{currency}} {{subtotal}}</td>\r\n												</tr>\r\n												<tr>\r\n														<td class=\"negrita fontSize1\"nowrap>Descuento:</td>\r\n														<td class=\"text-align-R fontSize1\" style=\"width:100px\"> {{discount}}</td>\r\n												</tr>\r\n												<tr>\r\n														<td class=\"negrita mayu fontSize1\">igv: 18%</td>\r\n														<td class=\"text-align-R fontSize1\">{{currencySymbol}} {{taxes}}</td>\r\n												</tr>\r\n												<tr>\r\n														<td class=\"negrita fontSize1\">Total:</td>\r\n														<td class=\"border-cell text-align-R fontSize1 negrita\"nowrap>{{currencySymbol}} {{amount}}</td>\r\n												</tr>\r\n										</table>\r\n								</td>\r\n						</tr>\r\n						<tr>\r\n								<td style=\"padding:5px;\" colspan=\"2\">SON: {{amountInWords}}</td>\r\n								<td>\r\n										<table>\r\n										 <tr>\r\n												<td style=\"padding:5px;\"nowrap>T/Cambio: {{exchangeRate}}</td>\r\n												<td style=\"padding-left:30px;\"nowrap>{{otherSymbol}} {{exchangeAmount}}</td>\r\n										 </tr>\r\n										</table>\r\n								</td>\r\n						</tr>\r\n\r\n				</table>\r\n				<div class=\"mayu\"></div>\r\n				{{#if company.bankAccount}}\r\n				<div class=\"banco\" style=\"display:inline-block\">\r\n						<p class=\"mayu negrita\">DEPOSITAR A NOMBRE DE:</p>\r\n						<p class=\"mayu negrita borderBottom\">{{company.companyName}}</p>\r\n						{{#each company.bankAccount}}\r\n						<table>\r\n								<tr>\r\n										<td><img src=\"{{image}}\"style=\"width:30px;height:17px;margin:3px;\"></td>\r\n										<td><p class=\"mayu negrita\" style=\"text-align:center;\">{{symbol}} {{number}}</p></td>\r\n								</tr>\r\n						</table>\r\n\r\n\r\n						{{/each}}\r\n				</div>\r\n				{{/if}}\r\n		</div>\r\n		<div class=\"bottom\">\r\n				{{#each company.footer}}\r\n						<p style=\"font-size:8px;\">{{this}}</p>\r\n				{{/each}}\r\n		</div>\r\n</body>\r\n		</div>\r\n\r\n</html>',
 2, '1', NULL, '1', NULL, now(), now());

 INSERT INTO `ms_templates` (`name`, `code`, `recipe`, `template`, `country_id`, `flag_default`, `type_document_id`, `flag_active`, `deleted_at`, `created_at`, `updated_at`)
VALUES ('Nota de Credito', 'nota-credito-pdf', 'phantom-pdf', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <meta http-equiv=\"X-UA-Compatible\" content=\"ie=edge\">\r\n    <link href=\"https://fonts.googleapis.com/css?family=Allerta+Stencil|Archivo|Noto+Sans\" rel=\"stylesheet\">\r\n    <title>Guia Comprobante</title>\r\n    <style>\r\n    * {\r\n        box-sizing: border-box;\r\n        font-family: \'Open Sans\', sans-serif;\r\n        margin: 0;\r\n        padding:0;\r\n        position: relative;\r\n    } \r\n    \r\n    th {\r\n        border-bottom: 1px solid  #000000;\r\n        padding: 5px;\r\n    }\r\n    \r\n    .container-credito {\r\n        padding: 40px 32px;\r\n    }\r\n    \r\n    .border-one {\r\n        border: solid 1px #000000;\r\n        border-radius: 8px;\r\n        height: 130px;\r\n        padding: 9px 14px;\r\n        width: 310px;\r\n    }\r\n    \r\n    .text-bold {\r\n        color: #000000;\r\n        font-size: 8px;\r\n        font-weight: bold;\r\n    }\r\n    \r\n    .text-regular {\r\n        color: #000000;\r\n        font-size: 8px;\r\n        font-weight: normal;\r\n    }\r\n    \r\n    .logo-qtc {\r\n        margin: 0 0 5px 45px;\r\n    }\r\n    \r\n    .border-two {\r\n      border-radius: 8px;\r\n      border: solid 1px #000000;\r\n      height: 268px;\r\n      padding: 15px;\r\n      position: absolute;\r\n      right: 35px;\r\n      top: 48px;\r\n      width: 272px;\r\n    }\r\n    \r\n    .border-three {\r\n        border-radius: 7px;\r\n        border: solid 1px #000000;\r\n        height: 127px;\r\n        margin-top: 9px;\r\n        padding: 15px;\r\n        width: 100%;\r\n    }\r\n    \r\n    .text-gray {\r\n        color: #5a5a5a;\r\n        font-size: 8px;\r\n    }\r\n    \r\n    .border {\r\n        background-color: #000000;\r\n        height: 1px;\r\n        margin-bottom: 13px;\r\n        width: 509px;\r\n    }\r\n    \r\n    .table-product {\r\n        border-bottom: 1px solid #000000;\r\n        border-left: 1px solid #000000;\r\n        border-right: 1px solid #000000;\r\n        border-top:1px solid #000000;\r\n        margin-top: 13px;\r\n    }\r\n    \r\n    .title-table {\r\n       color: #000000;\r\n       font-size: 8px;\r\n    }\r\n    \r\n    .border-four {\r\n      border: solid 1px #000000;\r\n      height: 120px;\r\n      margin-top: 5px;\r\n      padding: 10px 13px;\r\n      width: 290px;\r\n    }\r\n     \r\n    .list-price {\r\n        color: #000000;\r\n        font-size: 7px;\r\n        margin-bottom: 3px;\r\n    }\r\n    \r\n    .container-price {\r\n        border: 1px solid #000000;\r\n        bottom: -1px;\r\n        padding: 6px;\r\n        position: absolute;        \r\n        right: 0;\r\n        width: 310px;\r\n    }\r\n    \r\n    .text-password {\r\n        color: #000000;\r\n        font-size: 9px;\r\n    }\r\n    \r\n    .code-bar {\r\n        height: 32px;\r\n        width: 220px;\r\n    }\r\n    \r\n    .text-id {\r\n        margin-left: 35px;\r\n    }\r\n    </style>\r\n</head>\r\n<body>\r\n    <div class=\"container-credito\">\r\n        <img src=\"https://user-images.githubusercontent.com/461124/48586138-19155b00-e8fd-11e8-91e9-e0fe35b7d1d6.png\" class=\"logo-qtc\">\r\n      <div class=\"border-one\">\r\n        <p  style=\"font-size: 11px; font-weight: bold\">SRI2</p>\r\n        <p  style =\"font-size: 11px; font-weight: bold; padding-top: 10px; padding-bottom: 10px\">SR</p>\r\n        <p class=\"text-bold\" style=\"margin-bottom: 10px\">Dirección Matriz : <span class=\"text-regular\">{{subsidiary.address}}</span></p>\r\n        <p class=\"text-bold\" style=\"margin-bottom: 10px\">Dirección Sucursal: <span class=\"text-regular\">{{subsidiary.address}}</span></span></p>\r\n        <p class=\"text-bold\">Contribuyente especial Nro <span class=\"text-bold\" style=\"padding-left: 60px\">{{subsidiary.specialContributor}}</span></p>\r\n        <p class=\"text-bold\" style=\"margin-top: 8px\">OBLIGADO A LLEVAR CONTABILIDAD <span class=\"text-bold\" style=\"padding-left: 20px\">{{subsidiary.flagAccount}}</span></p>\r\n     </div>\r\n     <div class=\"border-two\">\r\n         <p style=\"font-size: 11px; font-weight: bold\">RUC {{subsidiary.ruc}}</p>\r\n         <p style=\"margin-top: 10px\">NOTA DE CRÉDITO</p>\r\n         <p style=\"font-size: 11px; font-weight: bold\">No {{number}}</p>\r\n         <p style=\"font-size: 11px; font-weight: bold; margin-top: 8px; margin-bottom: 8px\">NÚMERO DE AUTORIZACIÓN</p>\r\n         <p class=\"text-bold\" style=\"margin-bottom: 10px\">{{authorizationNumber}}</p>\r\n         <p class=\"text-regular\">FECHA DE AUTORIZACIÓN <span>{{authorizationDate}}</span></p>\r\n         <p style=\"font-size: 11px; font-weight: bold;margin-top: 5px; margin-bottom: 5px\">AMBIENTE: <span class=\"text-regular\" style=\"margin-left: 10px\">{{environment}}</span></p>\r\n         <p style=\"font-size: 11px; font-weight: bold; margin-bottom: 8px\">EMISIÓN: <span class=\"text-regular\" style=\"margin-left: 22px\">{{emission}}</span></p>\r\n         <p style=\"font-size: 14px; margin-bottom: 10px\">CLAVE DE ACCESO</p>\r\n         <img src=\"{{urlPassword}}\" class=\"code-bar\">\r\n         <p class=\"text-password\">{{password}}</p>\r\n     </div>\r\n     <div class=\"border-three\">\r\n         <p class=\"text-regular\" style=\"margin-bottom: 11px\">RAZÓN SOCIAL/NOMBRE O APELLIDO : <span class=\"text-gray\">{{customer.typePerson.fullName}}</span><span class=\"text-id\">Identificación: </span><span class=\"text-gray\">{{customer.typePerson.documentNumber}}</span></p>\r\n         <p class=\"text-regular\" style=\"margin-bottom: 7px\">FECHA DE EMISIÓN: <span class=\"text-gray\">{{documents.createdAtSri}}</span></p>\r\n         <p class=\"border\"></p>\r\n         <p class=\"text-regular\">COMPROBANTE QUE SE MODIFICA <span class=\"text-gray\">FACTURA</span></p>\r\n        <p class=\"text-regular\" style=\"margin-top: 7px; margin-bottom: 7px\">FECHA DE EMISIÓN (COMPROBANTE A MODIFICAR) <span class=\"text-gray\">{{documents.createdAtSri}}</span></p>\r\n        <p class=\"text-regular\">RAZÓN DE MODIFICACIÓN <span class=\"text-gray\">{{commentary}}</span> </p>\r\n     </div>\r\n     <table class=\"table-product\" width=\"100%\" cellpadding=\"0\" cellspacing=\"0\">\r\n         <tbody>\r\n             <tr>\r\n                 <th class=\"title-table\">Cod Principal</th>\r\n                 <th class=\"title-table\">Cod Auxiliar</th>\r\n                 <th class=\"title-table\">Cant</th>\r\n                 <th class=\"title-table\">Descripción</th>\r\n                 <th class=\"title-table\">Precio Unitario</th>\r\n                 <th class=\"title-table\">Descuento</th>\r\n                 <th class=\"title-table\">Subtotal</th>\r\n                 <th class=\"title-table\">Precio Total</th>\r\n             </tr>\r\n            {{#each details}}\r\n             <tr>\r\n                 <td class=\"text-gray\" style=\"text-align: center ; padding: 4px\">{{product.code}}</td>\r\n                 <td class=\"text-gray\" style=\"text-align: center; padding: 4px\">{{product.autoBarcode}}</td>\r\n                 <td class=\"text-gray\" style=\"text-align: center; padding: 4px\">{{quantity}}</td>\r\n                 <td class=\"text-gray\" style=\"text-align: center; padding: 4px\">{{product.description}}</td>\r\n                 <td class=\"text-gray\" style=\"text-align: center; padding: 4px\">{{price}}</td>\r\n                 <td class=\"text-gray\" style=\"text-align: center; padding: 4px\">{{discount}}</td>\r\n                 <td class=\"text-gray\" style=\"text-align: center; padding: 4px\">{{warProductsId}}</td>\r\n                 <td class=\"text-gray\" style=\"text-align: center; padding: 4px\">{{@root.subtotal}}</td>\r\n             </tr>\r\n            {{/each}}\r\n         </tbody>\r\n     </table>\r\n     <div>\r\n       <div class=\"border-four\">\r\n         <p class=\"text-bold\" style=\"margin-bottom: 20px\">INFORMACIÓN ADICIONAL</p>\r\n         <p class=\"text-bold\" style=\"margin-bottom: 20px\">DIRECCIÓN <span class=\"text-gray\"></span></p>\r\n         <p class=\"text-bold\" style=\"margin-bottom: 20px\">E-MAIL <span class=\"text-gray\"></span> </p>\r\n       </div>\r\n       <div class=\"container-price\">\r\n           <p class=\"list-price\">Subtotal 12% <span class=\"text-regular\" style=\"position:relative ; left: 185px\">S/. {{totalTaxesAmount.subtotal}}</span></p>\r\n           <p class=\"list-price\">Subtotal IVA 0% <span class=\"text-regular\" style=\"position:relative ; left: 175px\">S/. {{totalTaxesAmount.subtotalIva}}</span></p>\r\n           <p class=\"list-price\">Subtotal No Objeto a IVA <span class=\"text-regular\" style=\"position:relative ; left: 146px\">S/. {{totalTaxesAmount.subtotalNoObjIva}}</span></p>\r\n           <p class=\"list-price\">Subtotal Extento a IVA <span class=\"text-regular\" style=\"position:relative ; left: 155px\">S/. {{totalTaxesAmount.subtotalWithotTax}}</span></p>\r\n           <p class=\"list-price\">Subtotal Sin Impuestos <span class=\"text-regular\" style=\"position:relative ; left: 153px\">S/. {{totalTaxesAmount.discount}}</span></p>\r\n           <p class=\"list-price\">ICE <span class=\"text-regular\" style=\"position:relative ; left: 221px\">S/. {{totalTaxesAmount.ice}}</span></p>\r\n           <p class=\"list-price\">IVA 12% <span class=\"text-regular\"style=\"position:relative ; left: 203px\">S/. {{totalTaxesAmount.iva}}</span></p>\r\n           <p class=\"list-price\">IRBPNR <span class=\"text-regular\" style=\"position:relative ; left: 207px\">S/.{{totalTaxesAmount.tip}}</span></p>\r\n           <p class=\"list-price\">Valor Total <span class=\"text-regular\" style=\"position:relative ; left: 196px\">S/. {{totalTaxesAmount.total}}</span></p>\r\n       </div>\r\n     </div>\r\n    </div>\r\n</body>\r\n</html>',
 1, '1', NULL, '1', NULL, now(), now());

