VALUES ('OIT', 'OIT002', 'chrome-pdf', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <meta http-equiv=\"X-UA-Compatible\" content=\"ie=edge\">\r\n   <link href=\"https://fonts.googleapis.com/css?family=Lato:400,400i,700,700i,900\" rel=\"stylesheet\">\r\n    <title>Report</title>\r\n\r\n    <style>\r\n        * {\r\n            box-sizing: border-box;\r\n            font-family: \'Lato\', sans-serif;\r\n        }\r\n\r\n        .italic {\r\n          font-style: italic;\r\n        }\r\n        \r\n\r\n        .bold {\r\n          font-weight: bold;\r\n        }\r\n        \r\n        .font-one {\r\n            font-size:11px !important;\r\n        }\r\n        \r\n        .header {\r\n            background-color: white;\r\n            border-bottom: 2px solid #05579b;\r\n            height: 118px;\r\n            padding-top: 50px;\r\n            padding-bottom: 90px;\r\n        }\r\n        \r\n        .blue {\r\n            color: #05579b !important;\r\n        }\r\n\r\n        .back-gray {\r\n          background-color: #f1f3f7 !important;\r\n        }\r\n        \r\n        hr {\r\n            background-color: #05579b;\r\n            border: none;\r\n            height: 1px;\r\n            margin-top: -41px;\r\n        }\r\n        \r\n        .main {\r\n            background-color: #f1f3f7;\r\n            padding: 10px 25px;\r\n        }\r\n        \r\n        .square {\r\n            border: 1px solid #05579b;\r\n            border-radius: 0px 10px 10px 0px;\r\n            display: inline-block;\r\n        }\r\n        \r\n        .one-square {\r\n            margin-right: 20px;\r\n            width: 430px;\r\n        }\r\n        \r\n        .date-generation {\r\n            display: inline-block;\r\n            border-right: 1px solid #05579b;\r\n            border-bottom: 1px solid #05579b;\r\n            height: 100%;\r\n            padding: 10px;\r\n            width: 215px;\r\n           \r\n        }\r\n        \r\n        .date {\r\n            border-bottom: 1px solid #d3dde6;\r\n            display: inline-block;\r\n            padding: 10px;\r\n            margin: 0px;\r\n            margin-left: -5px;\r\n            text-align: center;\r\n            width: 210px;\r\n        }\r\n        \r\n        .buy {\r\n            display: inline-block;\r\n            border-right: 1px solid #05579b;    \r\n            padding: 10px;\r\n            width: 215px;\r\n        }\r\n        \r\n        .buy-two {\r\n            display: inline-block;\r\n            padding: 10px;\r\n            text-align: center;\r\n            width: 200px;\r\n        }\r\n        \r\n        .number-oit {\r\n            border-right: 1px solid #05579b;\r\n            display: inline-block;\r\n            padding: 10px;\r\n            width: 140px;\r\n        }\r\n        \r\n        .oit-two {\r\n            border-bottom: 1px solid #05579b;\r\n        }\r\n        \r\n        .oit-three {\r\n            border-bottom: 1px solid #d3dde6;\r\n            display: inline-block;\r\n            padding: 10px;\r\n            margin-left: -5px;\r\n            width: 150px;\r\n        }\r\n        \r\n        .text-align {\r\n            text-align: center !important;\r\n        }\r\n        \r\n        .oit-four {\r\n            display: inline-block;\r\n            text-align: center;\r\n            width: 150px;\r\n        }\r\n        \r\n        .table-date td {\r\n            border:1px solid #05579b;\r\n            padding:10px;\r\n        }\r\n        \r\n        .title {\r\n            background:#05579b;\r\n            color:white !important;\r\n            text-align:center !important;\r\n        }\r\n        \r\n         .table-date tr > td:nth-child(1), .table-work tr > td:nth-child(1), .table-activity tr > td:nth-child(1), .table-tools tr > td:nth-child(1), .table-cost tr > td:nth-child(1), .table-schedule tr > td:nth-child(1){\r\n             border-right:2px solid #05579b !important;\r\n             color:#05579b;\r\n             padding: 10px;\r\n             padding-left:25px;\r\n             text-align:left;\r\n             width:250px;\r\n           \r\n         }\r\n         \r\n          .table-date tr> td:nth-child(2), .table-work tr > td:nth-child(2), .table-activity tr > td:nth-child(2), .table-tools tr > td:nth-child(2), \r\n          .table-tools tr> td:nth-child(3), .table-tools tr> td:nth-child(4), .table-cost tr > td:nth-child(2), .table-cost tr > td:nth-child(3), .table-schedule tr > td:nth-child(2){\r\n             background:white;\r\n             border-color:#d3dde6;\r\n             border-right:2px solid #05579b !important;\r\n             color:#8a8a8a;\r\n             padding-left:20px;\r\n             text-align:left;\r\n         }\r\n         \r\n         \r\n         .back-whie {\r\n             background:white !important;\r\n         }\r\n\r\n         .table-work, .table-date, .table-activity, .table-tools, .table-cost, .table-schedule, .table-note{\r\n           border: 1px solid #05579b;\r\n           border-spacing: 0;\r\n           border-radius: 0px 0px 1em 1em;\r\n           font-size:11px;\r\n           overflow: hidden;\r\n           margin-bottom: 40px;\r\n           margin-top:40px;\r\n           width: 100%;\r\n         }\r\n\r\n         .table-work td, .table-date td, .table-activity td,  .table-tools td, .table-cost td, .table-schedule td, .table-note td{\r\n           border: 1px solid #05579b;\r\n         }\r\n\r\n        .table-date tr:last-child td:last-child {\r\n          height: 156px;\r\n        }\r\n\r\n        .table-note tr:last-child td:last-child {\r\n          height: 150px;\r\n        }\r\n\r\n        img {\r\n          background: white;\r\n          height: 62px;\r\n          padding-right: 10px;\r\n          width: 188px;\r\n        }\r\n\r\n        .bor-bot-blue {\r\n          border-bottom: 1px solid #05579b !important;\r\n        }\r\n        \r\n        .table-work , .table-schedule{\r\n            margin-bottom:80px !important;\r\n            margin-top:200px !important;\r\n        }\r\n        \r\n        .bor-top-blue {\r\n          border-top: 1px solid #05579b !important;\r\n        }\r\n        \r\n        .notes {\r\n          padding-left:20px;\r\n        }\r\n        \r\n        .font-gray {\r\n            color:#8a8a8a;\r\n        }\r\n        \r\n        .table-activity tr:nth-child(3) > td:nth-child(2) {\r\n            border-top: 1px solid #05579b !important;\r\n        } \r\n        \r\n        .table-activity tr:last-child > td:nth-child(2) {\r\n            border-bottom: 1px solid #05579b !important;\r\n        } \r\n        \r\n        .border-blue {\r\n            border:1px solid #05579b !important;\r\n        }\r\n        \r\n        .table-tools tr:nth-child(3) > td:nth-child(2), \r\n        .table-tools tr:nth-child(3) >td:nth-child(3), \r\n        .table-tools tr:nth-child(3) >td:nth-child(4),\r\n        .table-cost tr:nth-child(3) > td:nth-child(2), \r\n        .table-cost tr:nth-child(3) > td:nth-child(3),\r\n        .table-schedule  tr:nth-child(3) > td:nth-child(2){\r\n            border-top: 1px solid #05579b !important;\r\n        }\r\n        \r\n        .table-tools tr:last-child > td:nth-child(2), \r\n        .table-tools tr:last-child >td:nth-child(3), \r\n        .table-tools tr:last-child >td:nth-child(4),\r\n        .table-cost tr:last-child > td:nth-child(2), \r\n        .table-cost tr:last-child > td:nth-child(3),\r\n        .table-schedule  tr:last-child > td:nth-child(2){\r\n            border-bottom: 1px solid #05579b !important;\r\n        }\r\n        \r\n        .title-note {\r\n            padding:10px;\r\n        }\r\n\r\n    </style>\r\n</head>\r\n\r\n<body>\r\n    <div class=\"header\">\r\n        <img src=\"https://s3.amazonaws.com/japi-admin/logo-disec.png\" alt=\"\">\r\n        <hr>\r\n    </div>\r\n    <div class=\"main\">\r\n        <p class=\"blue italic font-one\">* datos obligatorios</p>\r\n        <div>\r\n            <div class=\"square one-square\">\r\n                <div>\r\n                    <div class=\"blue date-generation bold font-one\"> Fecha de generación</div>\r\n                    <div class=\"date font-one font-gray\">{{createdAt}}</div>\r\n                </div>\r\n                <div>\r\n                    <div class=\"blue buy bold font-one\">Nº de Orden de Compra (Opcional)</div>\r\n                    <div class=\"buy-two font-one font-gray\">{{orderPurchases}}</div>\r\n                </div>\r\n            </div>\r\n\r\n            <div class=\"square square-two\">\r\n                <div>\r\n                    <span class=\"blue number-oit oit-two bold font-one\"> OIT Nº</span>\r\n<span class=\"oit-three text-align font-one font-gray\">{{numberOit}}</span>\r\n  </div>\r\n <div>\r\n                    <div class=\"blue number-oit bold font-one\">Nº Cotización*</div>\r\n                    <div class=\"oit-four text-align font-one font-gray\">{{numberQuotation}}</div>\r\n                </div>\r\n            </div>\r\n        </div>\r\n    <table class=\"table-date text-align\">\r\n	   <tr>\r\n    	    <td colspan=\"2\" class=\"title\">Datos del Cliente</td>\r\n	   </tr>\r\n	   <tr>\r\n    	    <td class=\"bold\">Nombre:</td>\r\n    	    <td class=\"bor-top-blue\">{{dataCustomer.name}}</td>\r\n	   </tr>\r\n	   <tr>\r\n    	    <td class=\"bold\">Dirección:</td>\r\n    	    <td class=\"bor-bot-blue\">{{dataCustomer.address}}</td>\r\n	   </tr>\r\n	    <tr>\r\n    	     <td colspan=\"2\" class=\"text-align\">Contacto que aprobó trabajo *:</td>\r\n	   </tr>\r\n	   <tr>\r\n    	    <td class=\"bold\">Nombre:</td>\r\n    	    <td class=\"bor-top-blue\"></td>\r\n	   </tr>\r\n	   <tr>\r\n    	    <td class=\"bold\">Área:</td>\r\n    	    <td class=\"bor-bot-blue\"></td>\r\n	   </tr>\r\n	    <tr>\r\n <td colspan=\"2\" class=\"text-align\">Contacto para coordinacion de ejecución *:</td>\r\n	   </tr>\r\n	    <tr>\r\n    	    <td class=\"bold\">Nombre:</td>\r\n    	    <td class=\"bor-top-blue\"></td>\r\n	   </tr>\r\n	    <tr>\r\n    	    <td class=\"bold\">Área:</td>\r\n    	    <td></td>\r\n	   </tr>\r\n	    <tr>\r\n    	    <td class=\"bold\">Correo:</td>\r\n    	    <td></td>\r\n	   </tr>\r\n	    <tr>\r\n    	    <td class=\"bold\">Teléfono:</td>\r\n    	    <td class=\"bor-bot-blue\"></td>\r\n	   </tr>\r\n	   <tr>\r\n    	     <td colspan=\"2\" class=\"text-align\">Entregables al cliente *:</td>\r\n	   </tr>\r\n     <tr>\r\n    	    <td class=\"bold\">Informes:</td>\r\n    	    <td class=\"bor-bot-blue\">{{dataCustomer.description}}</td>\r\n	   </tr>\r\n	</table>\r\n\r\n    <table class=\"table-work\">\r\n      <tr>\r\n        <td class=\"text-align title\" colspan=\"2\">Datos del Trabajo</td>\r\n      </tr>\r\n      <tr>\r\n        <td class=\"bold\">Referencia*:</td>\r\n        <td class=\"bor-top-blue\">{{dataWork.reference}}</td>\r\n      </tr>\r\n      <tr>\r\n        <td class=\"bold\">Tiempo de ejecución del proyecto*:</td>\r\n        <td>{{dataWork.timeEjecution}}</td>\r\n      </tr>\r\n       <tr>\r\n        <td class=\"bold\">Nª de Técnicos Estimados:</td>\r\n        <td>{{dataWork.numberTechnical}}</td>\r\n      </tr>\r\n      <tr>\r\n        <td class=\"bold\">Contacto de Visita Tecnica: <br> (Opcional)</td>\r\n        <td>{{dataWork.contactTechnical}}</td>\r\n      </tr>\r\n      <tr>\r\n        <td class=\"bold\">Contacto de coordinación <br> de ejecución *:</td>\r\n        <td>{{dataWork.contactEjecution}}</td>\r\n      </tr>\r\n      <tr>\r\n        <td class=\"bold\">Ficha de Inicio (opcional):</td>\r\n        <td>{{dataWork.dateStart}}</td>\r\n      </tr>\r\n      <tr>\r\n        <td class=\"bold\">Fecha de Final (opcional):</td>\r\n        <td class=\"bor-bot-blue\">{{dataWork.dateEnd}}</td>\r\n      </tr>\r\n    </table>\r\n    <table class=\"table-activity\">\r\n       <tr>\r\n         <td class=\"title\" colspan=\"2\">Trabajo a Realizar</td>\r\n       </tr>\r\n       <tr>\r\n         <td class=\"bold\">ITEMS:</td>\r\n         <td class=\"text-align blue back-gray bor-bot-blue bold bor-top-blue\">Actividades</td>\r\n       </tr>\r\n        {{#each activities}}\r\n       <tr>\r\n         <td>{{id}}</td>\r\n         <td>{{description}}</td>\r\n       </tr>\r\n       {{/each}}\r\n    </table>\r\n    <table class=\"table-tools\">\r\n      <tr>\r\n         <td class=\"title\" colspan=\"4\">\r\n           Herramientas, Materiales y Servicios Requeridos\r\n         </td>\r\n      </tr>\r\n      <tr>\r\n        <td class=\"bold\">ITEMS</td>\r\n        <td class=\"text-align blue back-gray bor-bot-blue bor-top-blue bold\">Articulo</td>\r\n        <td class=\"text-align blue back-gray bor-bot-blue bor-top-blue bold\">UND</td>\r\n        <td class=\"text-align blue back-gray bor-bot-blue bor-top-blue bold\">CANT</td>\r\n      </tr>\r\n      {{#each toolsServiceRequired}}\r\n      <tr>\r\n        <td>{{id}}</td>\r\n        <td>{{productName}}</td>\r\n        <td>{{productUnid}}</td>\r\n        <td>{{quantity}}</td>\r\n      </tr>\r\n      {{/each}}\r\n    </table>\r\n    <table class=\"table-cost\">\r\n      <tr>\r\n        <td class=\"title\" colspan=\"3\">Gastos</td>\r\n      </tr>\r\n      <tr>\r\n        <td class=\"bold\">ITEMS</td>\r\n        <td class=\"text-align back-gray blue bor-bot-blue bor-top-blue bold\">Servicios</td>\r\n        <td class=\"text-align back-gray blue bor-bot-blue bor-top-blue bold\">Monto</td>\r\n      </tr>\r\n      {{#each expenses}}\r\n      <tr>\r\n        <td>{{id}}</td>\r\n        <td>{{name}}</td>\r\n        <td>{{amount}}</td>\r\n      </tr>\r\n      {{/each}}\r\n    </table>\r\n    <table class=\"table-schedule\">\r\n      <tr>\r\n        <td class=\"title\" colspan=\"2\">Cronograma de Ejecuciòn</td>\r\n      </tr>\r\n      <tr>\r\n        <td class=\"bold\">Fechas:</td>\r\n        <td class=\"text-align back-gray blue bor-bot-blue bor-top-blue bold\">Horas</td>\r\n      </tr>\r\n       {{#each schedules}}\r\n       <tr>\r\n        <td>{{date}}</td>\r\n        <td>{{hours}}</td>\r\n      </tr>\r\n       {{/each}}\r\n    </table>\r\n    <table class=\"table-note\">\r\n      <tr>\r\n        <td class=\"title title-note\" colspan=\"2\">Notas</td>\r\n      </tr>\r\n      <tr>\r\n        <td class=\"back-whie notes\">{{notes}}</td>\r\n      </tr>\r\n    </table>\r\n    </div> \r\n</body>\r\n\r\n</html>',
 1, '0', NULL, '1', NULL, now(), now());

 INSERT INTO `ms_templates` (`name`, `code`, `recipe`, `template`, `country_id`, `flag_default`, `type_document_id`, `flag_active`, `deleted_at`, `created_at`, `updated_at`)
VALUES ('Factura por Categorias', 'pdf-categories', 'phantom-pdf', '<html>\r\n\r\n<head>\r\n	<meta content=\"text/html; charset=utf-8\" http-equiv=\"Content-Type\">\r\n	<meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\r\n<style>\r\n	 *{font-size: 10px;border-collapse: collapse;}\r\n	 h1{font-size: 20px;}\r\n	 p{margin: 0;}\r\n	 .container{padding-bottom: 50px;}\r\n	 .fecha{font-size: 10px; text-align: right; margin-bottom: 9px;}\r\n	 .cabecera{width: 100%; height: 12%;}\r\n	 .content{width: 100%;margin-bottom: 20px;}\r\n	 .pie{width: 100%;vertical-align: top;}\r\n	 .negrita{font-weight: bold;}\r\n	 .mayu{text-transform: uppercase;}\r\n	 .amarillo{background-color: #eded55;}\r\n	 .azul{background-color: #7fceef;}\r\n	 .border-cell{border: solid 1px black;}\r\n	 .text-align-R{text-align: right;}\r\n	 .t-cliente{margin-right: 40px; width: 420px;}\r\n	 .t-vendedor tr td{padding: 2px;}\r\n	 .content {padding-top:6px;}\r\n	 .content table{width: 100%;}\r\n	 .content table tr td{ border: 1px solid black; text-align: center;}\r\n\r\n	 .t-pie table{font-size: 10px;}\r\n\r\n	 .banco{margin: 15px 70px;border: solid 1px black;width: 40%;}\r\n	 .borderBottom{border-bottom: solid 1px black;}\r\n	 .spacer{width: 340px;}\r\n	 .marginleft{30px;}\r\n	 .spacer1{width: 200px;}\r\n	 .spacer2{width: 220px;}\r\n	 .peso{width: 39px;}\r\n	#peso{ border-left: 1px solid black;\r\n					border-right: 1px solid black;\r\n					border-bottom: 1px solid black;}\r\n	 .content .noBorder tr td{border: none;}\r\n	 .bottom{position: absolute; bottom: 0;}\r\n	 .fontSize1{font-size: 11px;}\r\n	 .totalWidth{width: 100px;}\r\n	</style>\r\n</head>\r\n\r\n<body>\r\n		<div class=\"container\">\r\n				<div style=\"text-align:center\">\r\n						<img src=\"{{company.logo}}\" style=\"width:700px;height:30px;margin-bottom:30px;\">\r\n				</div>\r\n				<div style=\"display:flex;justify-content:center;\">\r\n						<h2 style=\"text-align:center;font-size: 18px;\">\r\n								<span style=\"borde