VALUES ('Remision guia', 'REM002', '', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <meta http-equiv=\"X-UA-Compatible\" content=\"ie=edge\">\r\n    <link href=\"https://fonts.googleapis.com/css?family=Allerta+Stencil|Archivo|Noto+Sans\" rel=\"stylesheet\">\r\n    <title>Guia Comprobante</title>\r\n    <style>\r\n    * {\r\n        box-sizing: border-box;\r\n        font-family: \'Open Sans\', sans-serif;\r\n        margin: 0;\r\n    }\r\n    \r\n    table {\r\n        border-collapse: collapse;\r\n    }\r\n    \r\n    td {\r\n        border: 1px solid #979797;\r\n        font-size: 9px;\r\n        padding: 9px;\r\n    }\r\n    \r\n    th {\r\n        background-color: #686868;\r\n        color: white;\r\n        font-size: 9px;\r\n        padding: 6px;\r\n    }\r\n    \r\n    \r\n    .reporte-cej {\r\n        padding: 31px 39px 0 39px ;\r\n    }\r\n    \r\n    .container-guia-remision {\r\n        border: 1px solid #000000;\r\n        height: 115px;\r\n        position: absolute;\r\n        right: 40px;\r\n        top: 62px;\r\n        width: 194px;\r\n    }\r\n    \r\n    .name-client {\r\n        color: #000000;\r\n        font-size: 11px;\r\n        font-weight: bold;\r\n        margin-top: 16px;\r\n        padding-bottom: 8px;\r\n    }\r\n    \r\n    .bar {\r\n        border: 1px solid #000000;\r\n        height: 1px;\r\n        width: 299.5px;\r\n    }\r\n    \r\n    .direction {\r\n        font-size: 12px;\r\n        font-weight: bold;\r\n        margin-top: 11px;\r\n    }\r\n    \r\n    .phone {\r\n        font-size: 10px;\r\n        margin: 5px 0 11.5px 0;\r\n    }\r\n    \r\n    .ruc {\r\n        border-bottom: 1px solid #000000;\r\n        font-size: 11px;\r\n        padding: 5px;\r\n        text-align: center;\r\n    }\r\n    \r\n    .remission {\r\n        color: #000000;\r\n        font-size: 18px;\r\n        font-weight: bold;\r\n        margin-top: 14px;\r\n        text-align: center;\r\n    }\r\n    \r\n    .number {\r\n        color: #000000;\r\n        font-size: 13px;\r\n        margin-top: 14px;\r\n        text-align: center;\r\n    }\r\n    \r\n    .table {\r\n        margin-top: 50px;\r\n        width: 100%;\r\n    }\r\n    \r\n    .text-gray {\r\n        color: #5a5a5a;\r\n        font-size: 9px;\r\n    }\r\n    \r\n    .table-product {\r\n        border-bottom: 1px solid #686868;\r\n        border-left: 1px solid #686868;        \r\n        border-right: 1px solid #686868;\r\n        margin-top: 15px;\r\n        width: 100%;\r\n    }\r\n    \r\n    .item-gray-table {\r\n        border: none;\r\n        color: #5a5a5a;\r\n        text-align: center;\r\n    }\r\n    \r\n    .container-date {\r\n        border: 1px solid #979797;\r\n        height: 42px;\r\n    }\r\n    \r\n    .date-carrier {\r\n        color: #000000;\r\n        font-size: 10px;\r\n        margin: 10.5px 0 5px 0;\r\n    }\r\n    \r\n    .name-razon {\r\n        border-bottom: 1px solid #979797;\r\n        color: #000000;\r\n        font-size: 9px;\r\n        padding: 5px;\r\n    }\r\n    \r\n    .ruc-date {\r\n        color: #000000;\r\n        font-size: 9px;\r\n        line-height: 1.56;\r\n        padding: 3px;\r\n    }\r\n    \r\n    .name-firme {\r\n        border-top: 1px solid #000000;\r\n        color: #5a5a5a;\r\n        display: inline-block;\r\n        font-size: 8px;\r\n        padding-top: 7.5px;\r\n        text-align: center;\r\n        width: 133px;\r\n    }\r\n    \r\n    .container-pp {\r\n        display: inline-block;\r\n        width: 49%;\r\n    }\r\n    \r\n    .container-firma {\r\n        margin-top: 81px;\r\n        text-align: center;\r\n    }\r\n    \r\n    .number-license {\r\n        color: #000000;\r\n        font-size: 9px;\r\n        padding: 5px;\r\n    }\r\n    \r\n    .border-bottom-table {\r\n        padding:5px;\r\n        width: 100%;\r\n         font-size: 10px;\r\n        border-bottom: 1px solid #979797;\r\n        border-left: 1px solid #979797;\r\n        border-right: 1px solid #979797;\r\n        padding-left: 15px;\r\n        \r\n    }\r\n    </style>\r\n</head>\r\n<body>\r\n    <div class=\"reporte-cej\">\r\n        <img src=\"https://user-images.githubusercontent.com/461124/48587254-74951800-e900-11e8-953f-749d967963ac.png\">\r\n        <div>\r\n            <p class=\"name-client\">{{sales.subsidiary.contactName}}</p>\r\n            <p class=\"bar\"></p>\r\n            <p class=\"direction\">{{sales.subsidiary.address}}</p>\r\n            <p class=\"phone\">TEL. {{driverCelphone}}</p>\r\n            <p class=\"bar\"></p>\r\n        </div>\r\n        <div class=\"container-guia-remision\">\r\n            <p class=\"ruc\">RUC {{sales.subsidiary.ruc}}</p>\r\n            <p class=\"remission\">Guia de Remisión</p>\r\n            <p class=\"number\">Nº {{number}}</p>\r\n        </div>\r\n        <table class=\"table\">\r\n            <tbody>\r\n                <tr>\r\n                    <td>Fecha de Inicio de traslado: <span class=\"text-gray\">{{createdAt}}</span></td>\r\n                    <td>RUC: <span class=\"text-gray\">{{sales.customer.ruc}}</span></td>\r\n                </tr>\r\n                <tr>\r\n                    <td>Destinatario: <span class=\"text-gray\" style=\"padding-left: 8px\">{{sales.customer.name}}</span></td>\r\n                    <td>Nº Doc Identidad <span class=\"text-gray\" style=\"padding-left: 11px\">{{sales.customer.typePerson.documentNumber}}</span> </td>\r\n                </tr>\r\n                <tr>\r\n                    <td>Punto de Partida: <span class=\"text-gray\">{{sales.subsidiary.address}}</span></td>\r\n                    <td>Punto de Llegada: <span class=\"text-gray\">{{destination}}</span> </td>\r\n                </tr>\r\n                <tr>\r\n                    <td colspan=\"2\">Motivo de Traslado: <span class=\"text-gray\">{{reason}}</span> </td>\r\n                </tr>\r\n            </tbody>\r\n        </table>\r\n        <table class=\"table-product\">\r\n            <tbody>\r\n                <tr>\r\n                    <th>CÓDIGO</th>\r\n                    <th>DESCRIPCIÓN</th>\r\n                    <th>CANTIDAD</th>\r\n                    <th>UND</th>\r\n                    <th>TOTAL</th>\r\n                </tr>\r\n                {{#each details}}\r\n                <tr>\r\n                    <td class=\"item-gray-table\">{{product.code}}</td>\r\n                    <td class=\"item-gray-table\">{{product.description}}</td>\r\n                    <td class=\"item-gray-table\">{{quantity}}</td>\r\n                    <td class=\"item-gray-table\">{{product.unitId}}</td>\r\n                    <td class=\"item-gray-table\">{{product.priceSoles}}</td>\r\n                </tr>\r\n                {{/each}}\r\n                    <div>\r\n                </div>\r\n            </tbody>\r\n        </table>\r\n        <div class=\"border-bottom-table\">Tipo y Número de Comprobante de pago:</div>\r\n        <div>\r\n            <div class=\"container-pp\">\r\n            <p class=\"date-carrier\">Datos de Transportista</p>\r\n            <div class=\"container-date\">\r\n                <p class=\"name-razon\">Nombre o Razón Social: <span class=\"text-gray\">{{driverName}}</span></p>\r\n                <p  class=\"ruc-date\">Ruc: <span class=\"text-gray\"></span></p>\r\n            </div>\r\n        </div>\r\n        <div class=\"container-pp\" style=\"margin-left: 8px\">\r\n            <p class=\"date-carrier\">Datos de la Unidad de Transporte y Conductor</p>\r\n            <div class=\"container-date\">\r\n                <p class=\"name-razon\">Marca y Nro de Placa: <span class=\"text-gray\">{{driverLicensePlate}}</span> </p>\r\n                <p class=\"number-license\">Nro de Licencia de Conducir: <span class=\"text-gray\">{{driverLicense}}</span></p>\r\n            </div>\r\n        </div>\r\n        </div>\r\n        <div class=\"container-firma\">\r\n            <p class=\"name-firme\">{{sales.customer.name}}</p>\r\n            <p class=\"name-firme\" style=\"margin-left: 24px\">Conformidad del Cliente</p>\r\n        </div>\r\n    </div>\r\n</body>\r\n</html>',
 1, '0', NULL, '1', NULL, now(), now());

  INSERT INTO `ms_templates` (`name`, `code`, `recipe`, `template`, `country_id`, `flag_default`, `type_document_id`, `flag_active`, `deleted_at`, `created_at`, `updated_at`)
VALUES ('Reporte de ventas por stock', 'ventas-por-stock', 'xlsx', '{{#xlsxAdd \"xl/worksheets/sheet1.xml\" \"worksheet.sheetData[0].row\"}}\r\n<row>\r\n    <c t=\"inlineStr\">\r\n        <is>\r\n            <t>Reporte de Ventas con Stock</t>\r\n        </is>\r\n    </c>\r\n</row>\r\n{{/xlsxAdd}}\r\n{{#xlsxAdd \"xl/worksheets/sheet1.xml\" \"worksheet.sheetData[0].row\"}}\r\n<row>\r\n    <c t=\"inlineStr\">\r\n        <is>\r\n            <t>Desde {{ startDate }} Hasta {{ endDate }} </t>\r\n        </is>\r\n    </c>\r\n</row>\r\n{{/xlsxAdd}}\r\n{{#xlsxAdd \"xl/worksheets/sheet1.xml\" \"worksheet.sheetData[0].row\"}}\r\n<row>{{#each columns}}\r\n    <c t=\"inlineStr\">\r\n        <is>\r\n            <t> {{name}} </t>\r\n        </is>\r\n    </c> {{/each}}\r\n</row>\r\n{{/xlsxAdd}} {{#each rows as |product|}} {{#xlsxAdd \"xl/worksheets/sheet1.xml\" \"worksheet.sheetData[0].row\"}}\r\n<row> {{#each ../columns}}\r\n    <c t=\"inlineStr\">\r\n        <is>\r\n            <t> {{lookup ../this (lookup this \'value\')}} </t>\r\n        </is>\r\n    </c> {{/each}} </row> {{/xlsxAdd}} {{/each}} {{{xlsxPrint}}}',
