VALUES ('Withholding tax', 'withholding-tax', 'phantom-pdf', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <meta http-equiv=\"X-UA-Compatible\" content=\"ie=edge\">\r\n    <link href=\"https://fonts.googleapis.com/css?family=Allerta+Stencil|Archivo|Noto+Sans\" rel=\"stylesheet\">\r\n    <title>Guia Comprobante</title>\r\n    <style>\r\n    * {\r\n        box-sizing: border-box;\r\n        font-family: \'Open Sans\', sans-serif;\r\n        margin: 0;\r\n    }\r\n    \r\n    td {\r\n        font-size: 8px;\r\n        padding: 8px;\r\n        text-align: left;\r\n    }   \r\n    \r\n    th {\r\n        border-bottom: 1px solid #000000;\r\n        padding: 5px;\r\n    }\r\n    \r\n    .name-cliente {\r\n        color: #000000;\r\n        font-size: 9px;\r\n        margin-bottom: 3px;\r\n    }\r\n    \r\n    .guia-remision {\r\n        border: solid 1px #000000;\r\n        border-radius: 8px;\r\n        height: 142px;\r\n        padding: 9px 15px 8px 15px;\r\n        width: 310px;\r\n    }\r\n    \r\n    .text-gray-ligth {\r\n        color: #5a5a5a;\r\n        font-size: 8px;\r\n    }\r\n    \r\n    .border-reporte {\r\n        border: solid 1px #000000;\r\n        border-radius: 8px;\r\n        height: 308px;\r\n        padding: 15px 12px 11px 12px;\r\n        position: absolute;\r\n        right: 36px;\r\n        top: 35px;\r\n        width: 282px;\r\n    }\r\n    \r\n    .text-bold {\r\n        font-size: 9px;\r\n        font-weight: bold;\r\n    }\r\n    \r\n    .logo {\r\n        height: 32px;\r\n        margin-bottom: 8px;\r\n        width: 240px;\r\n    }\r\n    \r\n    .logo-qtc {\r\n        margin: 0 0 20px 70px;\r\n    }\r\n    \r\n    .container-networks {\r\n        border: solid 1px #000000;\r\n        border-radius: 7px;\r\n        height: 60px;\r\n        margin-top: 10px;\r\n        padding: 15px;\r\n        width: 100%;\r\n    }\r\n    \r\n    .title-table {\r\n        color: #000000;\r\n        font-size: 8px;\r\n        text-align: center;\r\n    }\r\n    \r\n    .container-information {\r\n        border: solid 1px #000000;\r\n        height: 122px;\r\n        margin-top: 5px;\r\n        padding: 20px 0 0 15px;\r\n        width: 290px;\r\n    }\r\n    \r\n    .text-password {\r\n        color: #000000;\r\n        font-size: 9px;\r\n    }\r\n    \r\n    .table-retencion {\r\n        border: 1px solid #000000;\r\n    }\r\n    </style>\r\n</head>\r\n<body>\r\n<div style=\"padding: 40px 32px 0 32px\">\r\n     <img src=\"https://user-images.githubusercontent.com/461124/48586138-19155b00-e8fd-11e8-91e9-e0fe35b7d1d6.png\" class=\"logo-qtc\"/>\r\n    <div class=\"guia-remision\">\r\n    <p class=\"name-cliente\" style=\"padding-bottom: 10px;font-weight: bold\">SRI1</p>\r\n    <p class=\"name-cliente\" style=\"padding-bottom: 10px;font-weight: bold\">SRI</p>\r\n    <p class=\"text-bold\" style=\"margin-bottom: 4px\">Dirección Matriz :  <span class=\"text-gray-ligth\">{{supplier.address}}</span></p>\r\n    <p class=\"text-bold\" style=\"padding-bottom: 15px\">Dirección Sucursal: <span class=\"text-gray-ligth\">{{supplier.address}}</span></p>\r\n    <p class=\"text-bold\" style=\"margin-bottom: 10px\">Contribuyente especial Nro <span class=\"text-gray-ligth\" style=\"margin-left: 93px\">{{subsidiary.specialContributor}}</span></p>\r\n    <p class=\"text-bold\">OBLIGADO A LLEVAR CONTABILIDAD <span class=\"text-gray-ligth\" style=\"margin-left: 48px\">{{subsidiary.flagAccount}}</span></p>\r\n     \r\n    <div class=\"border-reporte\">\r\n     <p class=\"text-ruc\" style=\"text-align: left\">RUC {{company.ruc}}</p>\r\n     <p style=\"font-size: 14px; text-align: left; margin-bottom: 7px; margin-top: 12px\">COMPROBANTE DE RETENCIÓN</p>\r\n     <p style=\"font-size: 11px; text-align: left; margin-bottom: 10px; margin-top: 9px\">Nº 002 - 001 - 123456789</p>\r\n     <p style=\"font-size: 11px; text-align: left; margin-bottom: 8px\">NÚMERO DE AUTORIZACIÓN</p>\r\n     <p style=\"font-size: 11px; text-align: left; margin-bottom: 10px\">{{withholdingRelated.authorizationNumber}}</p>\r\n     <p style=\"font-size:9px;padding-bottom: 7px\">FECHA DE AUTORIZACIÓN <span class=\"text-gray-ligth\" style=\"padding-left: 8px\">{{withholdingRelated.authorizationDate}}</span></p>\r\n     <p style=\"font-size: 11px; text-align: left; padding-bottom:7px\">AMBIENTE: <span class=\"text-gray-ligth\">{{withholdingRelated.environment}}</span></p>\r\n     <p style=\"font-size: 11px; text-align: left; padding-bottom:7px\">EMISIÓN: <span class=\"text-gray-ligth\">{{withholdingRelated.emission}}</span></p>\r\n     <p style=\"font-size: 14px; padding-bottom: 10px\">CLAVE DE ACCESO</p>\r\n     <img src=\"{{withholdingRelated.urlPassword}}\" class=\"logo\"/>\r\n     <p class=\"text-password\">{{withholdingRelated.password}}</p>\r\n    </div>\r\n </div>\r\n <div class=\"container-networks\">\r\n        <p class=\"text-bold\">RAZÓN SOCIAL/NOMBRE O APELLIDO : <span class=\"text-gray-ligth\" style=\"padding-left: 5px\">{{supplier.name}}</span> <span style=\"margin-left: 35px\">IDENTIFICACIÓN: </span> <span class=\"text-gray-ligth\" style=\"padding-left: 5px\">{{supplier.documentNumber}}</span> </p>\r\n        <p class=\"text-bold\" style=\"margin-top: 11px\">FECHA DE EMISIÓN :<span class=\"text-gray-ligth\" style=\"padding-left: 5px\">{{createdAtSri}}</span></p>\r\n    </div>\r\n    <table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" heigth=\"154px\" style=\"margin-top: 9px\" class=table-retencion>\r\n        <tbody>\r\n            <tr>\r\n                <th class=\"title-table\">Comprobante</th>\r\n                <th class=\"title-table\">Número</th>\r\n                <th class=\"title-table\">Fecha de Emisión</th>\r\n                <th class=\"title-table\">Ejercicio Fiscal</th>\r\n                <th class=\"title-table\">Base Imponible para la Retención</th>\r\n                <th class=\"title-table\">Impuestos</th>\r\n                <th class=\"title-table\">Pocentaje de Retención</th>\r\n                <th class=\"title-table\">Valor Retenido</th>\r\n            </tr>\r\n            {{#each withholdingRelated.details}}\r\n            <tr>\r\n                <td class=\"text-gray-ligth\" style=\"text-align: center\">{{@root.typeDocument.name}}</td>\r\n                <td class=\"text-gray-ligth\" style=\"text-align: center\">{{@root.withholdingRelated.numberOperation}}</td></td>\r\n                <td class=\"text-gray-ligth\" style=\"text-align: center\">{{@root.createdAtSri}}</td>\r\n                <td class=\"text-gray-ligth\" style=\"text-align: center\">{{@root.periodTax}}</td>\r\n                <td class=\"text-gray-ligth\" style=\"text-align: center\">{{baseSubtotal}}</td>\r\n                <td class=\"text-gray-ligth\" style=\"text-align: center\">{{codeTax}}</td>\r\n                <td class=\"text-gray-ligth\" style=\"text-align: center\">{{percentageTax}}</td>\r\n                <td class=\"text-gray-ligth\" style=\"text-align: center\">{{@root.subTotal}}</td>\r\n            </tr>\r\n            {{/each}}\r\n        </tbody>\r\n    </table>\r\n    <div class=\"container-information\">\r\n        <p class=\"text-bold\">INFORMACIÓN ADICIONAL</p>\r\n        <p class=\"text-bold\" style=\"margin-top: 15px\">DIRECCIÓN <span class=\"text-gray-ligth\" style=\"margin-left:20px\"></span></p>\r\n        <p class=\"text-bold\" style=\"margin-top: 15px\">E-MAIL <span class=\"text-gray-ligth\" style=\"margin-left:42px\"></span></p>\r\n        </div>\r\n    </div>\r\n</body>\r\n</html>',
 1, '1', NULL, '1', NULL, now(), now());

